package bitmap.transformer;

public class App {
    public static void main(String[] args) {
        // Instantiate the bmp image
        Bitmap bitmap = new Bitmap("java_clr_hori.bmp");

        // Transform image
        bitmap.crazy();

        // Write out image
        bitmap.save("image.bmp");
    }
}
